mapper.select:
		 select 
 		  
 		  from area_tb area
 		  where area.id = #{ id }

mapper.delete:
		 delete 
 		  from area_tb
 		  where id = #{ id }

mapper.select:
		 select 
 		  
 		  from area_tb area
 		  where 1=1 
 		 
 			 and area.id = #{id}
 		  
 		 
 			 and area.create_time = #{createTime}
 		  
 		 
 			 and area.creator = #{creator}
 		  
 		 
 			 and area.creator_id = #{creatorId}
 		  
 		 
 			 and area.valid = #{valid}
 		  
 		 
 			 and area.update_time = #{updateTime}
 		  
 		 
 			 and area.updator = #{updator}
 		  
 		 
 			 and area.updator_id = #{updatorId}
 		  
 		 
 			 and area.pid = #{pid}
 		  
 		 
 			 and area.short_map_name = #{shortMapName}
 		  
 		 
 			 and area.short_name = #{shortName}
 		  
 		 
 			 and area.spelling = #{spelling}
 		  
 		 
 			 and area.state = #{state}
 		  
 		 
 			 and area.type = #{type}
 		  
 		 
 			 and area.zip = #{zip}
 		  
 		 
 			 and area.name = #{name}

		 select 
 		  count(area.id)
 		  from area_tb area
 		  where 1=1 
 		 
 			 and area.id = #{id}
 		  
 		 
 			 and area.create_time = #{createTime}
 		  
 		 
 			 and area.creator = #{creator}
 		  
 		 
 			 and area.creator_id = #{creatorId}
 		  
 		 
 			 and area.valid = #{valid}
 		  
 		 
 			 and area.update_time = #{updateTime}
 		  
 		 
 			 and area.updator = #{updator}
 		  
 		 
 			 and area.updator_id = #{updatorId}
 		  
 		 
 			 and area.pid = #{pid}
 		  
 		 
 			 and area.short_map_name = #{shortMapName}
 		  
 		 
 			 and area.short_name = #{shortName}
 		  
 		 
 			 and area.spelling = #{spelling}
 		  
 		 
 			 and area.state = #{state}
 		  
 		 
 			 and area.type = #{type}
 		  
 		 
 			 and area.zip = #{zip}
 		  
 		 
