mapper.select:
		 select 
 		  
 		  from goods_brand_tb goodsBrand
 		  where goodsBrand.id = #{ id }

mapper.delete:
		 delete 
 		  from goods_brand_tb
 		  where id = #{ id }

mapper.select:
		 select 
 		  
 		  from goods_brand_tb goodsBrand
 		  where 1=1 
 		 
 			 and goodsBrand.id = #{id}
 		  
 		 
 			 and goodsBrand.brand_number = #{brandNumber}
 		  
 		 
 			 and goodsBrand.brand_name = #{brandName}
 		  
 		 
 			 and goodsBrand.brand_english_name = #{brandEnglishName}
 		  
 		 
 			 and goodsBrand.brand_initial = #{brandInitial}
 		  
 		 
 			 and goodsBrand.state = #{state}
 		  
 		 
 			 and goodsBrand.is_delete = #{isDelete}
 		  
 		 
 			 and goodsBrand.directly_org_id = #{directlyOrgId}
 		  
 		 
 			 and goodsBrand.hierarchy_id = #{hierarchyId}
 		  
 		 
 			 and goodsBrand.creator_id = #{creatorId}
 		  
 		 
 			 and goodsBrand.creator = #{creator}
 		  
 		 
 			 and goodsBrand.create_time = #{createTime}
 		  
 		 
 			 and goodsBrand.updator_id = #{updatorId}
 		  
 		 
 			 and goodsBrand.updator = #{updator}
 		  
 		 
 			 and goodsBrand.update_time = #{updateTime}
 		  
 		 
 			 and goodsBrand.extends_params = #{extendsParams}
 		  
 		 
 			 and goodsBrand.remark1 = #{remark1}
 		  
 		 
 			 and goodsBrand.remark2 = #{remark2}

		 select 
 		  count(goodsBrand.id)
 		  from goods_brand_tb goodsBrand
