mapper.select:
		 select 
 		  
 		  from goods_category_tb goodsCategory
 		  where goodsCategory.id = #{ id }

mapper.delete:
		 delete 
 		  from goods_category_tb
 		  where id = #{ id }

mapper.select:
		 select 
 		  
 		  from goods_category_tb goodsCategory
 		  where 1=1 
 		 
 			 and goodsCategory.id = #{id}
 		  
 		 
 			 and goodsCategory.category_id = #{categoryId}
 		  
 		 
 			 and goodsCategory.category_name = #{categoryName}
 		  
 		 
 			 and goodsCategory.parent_id = #{parentId}
 		  
 		 
 			 and goodsCategory.category_type = #{categoryType}
 		  
 		 
 			 and goodsCategory.category_level = #{categoryLevel}
 		  
 		 
 			 and goodsCategory.input_tax = #{inputTax}
 		  
 		 
 			 and goodsCategory.output_tax = #{outputTax}
 		  
 		 
 			 and goodsCategory.consume_tax = #{consumeTax}
 		  
 		 
 			 and goodsCategory.hierarchy_id = #{hierarchyId}
 		  
 		 
 			 and goodsCategory.directly_org_id = #{directlyOrgId}
 		  
 		 
 			 and goodsCategory.order_number = #{orderNumber}
 		  
 		 
 			 and goodsCategory.status = #{status}
 		  
 		 
 			 and goodsCategory.is_delete = #{isDelete}
 		  
 		 
 			 and goodsCategory.creator_id = #{creatorId}
 		  
 		 
 			 and goodsCategory.creator = #{creator}
 		  
 		 
 			 and goodsCategory.create_time = #{createTime}
 		  
 		 
 			 and goodsCategory.updator_id = #{updatorId}
 		  
 		 
