mapper.select:
		 select 
 		  
 		  from goods_tb goods
 		  where goods.id = #{ id }

mapper.delete:
		 delete 
 		  from goods_tb
 		  where id = #{ id }

mapper.select:
		 select 
 		  
 		  from goods_tb goods
 		  where 1=1 
 		 
 			 and goods.id = #{id}
 		  
 		 
 			 and goods.goods_name = #{goodsName}
 		  
 		 
 			 and goods.goods_short_name = #{goodsShortName}
 		  
 		 
 			 and goods.goods_spu = #{goodsSpu}
 		  
 		 
 			 and goods.goods_type = #{goodsType}
 		  
 		 
 			 and goods.self_support = #{selfSupport}
 		  
 		 
 			 and goods.goods_category_id = #{goodsCategoryId}
 		  
 		 
 			 and goods.is_multi_properties = #{isMultiProperties}
 		  
 		 
 			 and goods.sku_nums = #{skuNums}
 		  
 		 
 			 and goods.sale_map = #{saleMap}
 		  
 		 
 			 and goods.state = #{state}
 		  
 		 
 			 and goods.is_delete = #{isDelete}
 		  
 		 
 			 and goods.directly_org_id = #{directlyOrgId}
 		  
 		 
 			 and goods.hierarchy_id = #{hierarchyId}
 		  
 		 
 			 and goods.creator_id = #{creatorId}
 		  
 		 
 			 and goods.creator = #{creator}
 		  
 		 
 			 and goods.create_time = #{createTime}
 		  
 		 
 			 and goods.updator_id = #{updatorId}
 		  
 		 
 			 and goods.updator = #{updator}
 		  
 		 
 			 and goods.update_time = #{updateTime}
 		  
 		 
 			 and goods.extends_params = #{extendsParams}
 		  
 		 
 			 and goods.remark1 = #{remark1}
 		  
 		 
 			 and goods.remark2 = #{remark2}

