mapper.select:
		 select 
 		  
 		  from goods_normal_price_tb goodsNormalPrice
 		  where goodsNormalPrice.id = #{ id }

mapper.delete:
		 delete 
 		  from goods_normal_price_tb
 		  where id = #{ id }

mapper.select:
		 select 
 		  
 		  from goods_normal_price_tb goodsNormalPrice
 		  where 1=1 
 		 
 			 and goodsNormalPrice.id = #{id}
 		  
 		 
 			 and goodsNormalPrice.sale_price = #{salePrice}
 		  
 		 
 			 and goodsNormalPrice.market_price = #{marketPrice}
 		  
 		 
 			 and goodsNormalPrice.purchase_price = #{purchasePrice}
 		  
 		 
 			 and goodsNormalPrice.goods_id = #{goodsId}
 		  
 		 
 			 and goodsNormalPrice.goods_sale_id = #{goodsSaleId}
 		  
 		 
 			 and goodsNormalPrice.store_id = #{storeId}
 		  
 		 
 			 and goodsNormalPrice.store_type = #{storeType}
 		  
 		 
 			 and goodsNormalPrice.store_name = #{storeName}
 		  
 		 
 			 and goodsNormalPrice.goods_state = #{goodsState}
 		  
 		 
 			 and goodsNormalPrice.is_delete = #{isDelete}
 		  
 		 
 			 and goodsNormalPrice.directly_org_id = #{directlyOrgId}
 		  
 		 
 			 and goodsNormalPrice.hierarchy_id = #{hierarchyId}
 		  
 		 
 			 and goodsNormalPrice.valid = #{valid}
 		  
 		 
 			 and goodsNormalPrice.creator_id = #{creatorId}
 		  
 		 
 			 and goodsNormalPrice.create_time = #{createTime}
 		  
 		 
 			 and goodsNormalPrice.updator_id = #{updatorId}
 		  
 		 
 			 and goodsNormalPrice.update_time = #{updateTime}
 		  
 		 
 			 and goodsNormalPrice.remark1 = #{remark1}
 		  
 		 
 			 and goodsNormalPrice.remark2 = #{remark2}

		 select 
 		  count(goodsNormalPrice.id)
 		  from goods_normal_price_tb goodsNormalPrice
 		  where 1=1 
 		 
 			 and goodsNormalPrice.id = #{id}
 		  
 		 
