mapper.select:
		 select 
 		  
 		  from sale_record_tb saleRecord
 		  where saleRecord.id = #{ id }

mapper.delete:
		 delete 
 		  from sale_record_tb
 		  where id = #{ id }

mapper.select:
		 select 
 		  
 		  from sale_record_tb saleRecord
 		  where 1=1 
 		 
 			 and saleRecord.id = #{id}
 		  
 		 
 			 and saleRecord.member_name = #{memberName}
 		  
 		 
 			 and saleRecord.pay_method = #{payMethod}
 		  
 		 
 			 and saleRecord.sale_number = #{saleNumber}
 		  
 		 
 			 and saleRecord.sale_price = #{salePrice}
 		  
 		 
 			 and saleRecord.receivable_amount = #{receivableAmount}
 		  
 		 
 			 and saleRecord.paid_amount = #{paidAmount}
 		  
 		 
 			 and saleRecord.change_amount = #{changeAmount}
 		  
 		 
 			 and saleRecord.cashier_name = #{cashierName}
 		  
 		 
 			 and saleRecord.is_delete = #{isDelete}
 		  
 		 
 			 and saleRecord.directly_org_id = #{directlyOrgId}
 		  
 		 
 			 and saleRecord.hierarchy_id = #{hierarchyId}
 		  
 		 
 			 and saleRecord.creator_id = #{creatorId}
 		  
 		 
 			 and saleRecord.creator = #{creator}
 		  
 		 
 			 and saleRecord.create_time = #{createTime}
 		  
 		 
 			 and saleRecord.updator_id = #{updatorId}
 		  
 		 
 			 and saleRecord.updator = #{updator}
 		  
 		 
 			 and saleRecord.update_time = #{updateTime}
 		  
 		 
 			 and saleRecord.remark1 = #{remark1}
 		  
 		 
 			 and saleRecord.remark2 = #{remark2}
 		  
 		 
 			 and saleRecord.remark3 = #{remark3}
 		  
 		 
 			 and saleRecord.remark4 = #{remark4}

		 select 
 		  count(saleRecord.id)
