mapper.select:
		 select 
 		  
 		  from system_user_role_tb systemUserRole
 		  
 		  
 		  	 systemUserRole.id = #{ id }
 		  
 		  
 		  	 and  systemUserRole.user_id = #{ userId }
 		  
 		  
 		  	 and  systemUserRole.role_id = #{ roleId }

mapper.delete:
		 delete 
 		  from system_user_role_tb
 		  where id = #{ id }

mapper.select:
		 select 
 		  
 		  from system_user_role_tb systemUserRole
 		  where 1=1 
 		 
 			 and systemUserRole.id = #{id}
 		  
 		 
 			 and systemUserRole.user_id = #{userId}
 		  
 		 
 			 and systemUserRole.role_id = #{roleId}

		 select 
 		  count(systemUserRole.id)
 		  from system_user_role_tb systemUserRole
 		  where 1=1 
 		 
 			 and systemUserRole.id = #{id}
 		  
 		 
 			 and systemUserRole.user_id = #{userId}
 		  
 		 
 			 and systemUserRole.role_id = #{roleId}
